head.script:
const PRICE = "PRICE"
const COUNT = "COUNT"
const TOTAL = "TOTAL"
const DATE = "DATE"
const SHOP_ID = "SHOP_ID"
const SHOP_NAME = "SHOP_NAME"
const SHOE_NAME = "SHOE_NAME"
const SIZE = "SIZE"
const INVENTORY = "INVENTORY"
const NO = "NO"
const STATUS = "STATUS"
	scwin.onpageload = function() {
		$p.executeSubmission("getDataShop");
		var currentURL = window.location.href;
		// split string by '&'
		var params = currentURL.split('&');
		//declare init shopID and shopName
		var shopID = ""
		var shopName = ""
		for( let i = 0; i < params.length ; i++){

			// split string by '='
			var param= params[i].split('=')
			// just recieve shopID and shopName
			if( param[0] == "shopID" ){
				shopID = param[1]
			}
			else if( param[0] == "shopName" ) shopName = decodeURIComponent(param[1])
		}
		//if param not apropriate then return
		if(shopID  == undefined || shopID =="" || shopName  == undefined || shopName =="") return
		// put into input
		scwin.getHeaderPage().ipt_ShopID.setValue(shopID)
		scwin.getHeaderPage().ipt_ShopName.setValue(shopName)
		scwin.search({
			SHOP_ID: shopID,
		})
	};

