	scwin.search= function(input){
		try {
			if(input.DATE!= undefined && input.DATE!= "" && !scwin.checkValidDate(input.DATE)) throw new Error("Invalid date \nCorrect format: YYYY-MM-DD")
			if(input.SHOP_NAME == "" ) throw new Error("Shop Name not empty")
			dl_Shoe_Shop.clearFilter()
			console.log("input: "+input)
			if( input.DATE  != undefined && input.DATE != ""  ){
				var filterOptions = {
				  type: 'row',       
				  colIndex: "DATE",    
				  key: input.DATE,    
				  condition: 'and',  
				  exactMatch: false  
				};
				dl_Shoe_Shop.setColumnFilter( filterOptions )
			}
			//dl_Shoe_Shop.setColumnFilter( {type:'regExp', colIndex:'SHOP_ID', key: input.SHOP_ID, condition:'and'});
			console.log( "type : " + typeof input.SIZE)
			//typeof Number(input.SHOP_ID)
			if( input.SHOP_ID  != undefined && input.SHOP_ID != ""  ){

				var filterOptions = {
				  type: 'row',       
				  colIndex: "SHOP_ID",    
				  key: input.SHOP_ID,    
				  condition: 'and',  

				};

				dl_Shoe_Shop.setColumnFilter( filterOptions )
			}
			if( input.SIZE  != undefined && input.SIZE != ""  ){
				var filterOptions = {
				  type: 'row',       
				  colIndex: "SIZE",    
				  key: input.SIZE,    
				  condition: 'and',  
				  exactMatch: false  
				};
				dl_Shoe_Shop.setColumnFilter( filterOptions )
			}
		} catch (e) {
			alert("ERR: "+ e.message)
		}
		scwin.setTotalRowCount()
	}
	scwin.addEventHandler= function() {
		try {
			const INDEX= 0;

	        var input= scwin.getHeaderPage().scwin.getInput()
	        if(!scwin.checkValidDate(input.DATE))
	        	throw new Error("Invalid date \nCorrect format: YYYY-MM-DD")
	        if(input.SHOP_NAME == undefined || input.SHOP_NAME == "")
	        	throw new Error("Must enter appropriate SHOP_ID")
	        if(!scwin.checkValidDate(input.DATE))
	        	throw new Error("Invalid date \nCorrect format: YYYY-MM-DD")
			dl_Shoe_Shop.insertRow(INDEX)
			dl_Shoe_Shop.setCellData(INDEX, DATE, input.DATE)
			dl_Shoe_Shop.setCellData(INDEX, SHOP_ID, input.SHOP_ID)
			dl_Shoe_Shop.setCellData(INDEX, SHOP_NAME, input.SHOP_NAME)
			dl_Shoe_Shop.setCellData(INDEX, STATUS, "insert")
			scwin.initDataShop()
		} catch (e) {
			alert("ERR: "+ e.message)
		}

	}
	scwin.deleteEventHandler= function(){
		var index= grv_Show_Figure.getFocusedRowIndex()
		if( index == -1 ){
			alert( "Choose column need to delete ")
			return;
		}
		dl_Shoe_Shop.deleteRow(index)
	}
	scwin.checkValid= function() {
		var row= dl_Shoe_Shop.getRowCount()
		var deletee = 0;
		var insert = 0;
		var update = 0;
		for(let i= 0; i < row; i++){
			// get CELL[PRICE] & CELL[COUNT] value
			var status= dl_Shoe_Shop.getCellData(i, STATUS)
			var shoeName= dl_Shoe_Shop.getCellData(i, SHOE_NAME)
			var size= dl_Shoe_Shop.getCellData(i, SIZE)
			var count= dl_Shoe_Shop.getCellData(i, COUNT)
			var inventory= dl_Shoe_Shop.getCellData(i, INVENTORY)
			// shoeName
			if(shoeName==undefined || shoeName == "")
				throw new Error(`ROW[${i+1}] field SHOE_NAME can't be blank`);
			// size
			if(size==undefined || size == "")
				throw new Error(`ROW[${i+1}] field SIZE can't be blank`);
			if(parseInt(size) < 0)
				throw new Error(`ROW[${i+1}] field SIZE can't be negative`);
			// count
			if(count==undefined || count == "")
				throw new Error(`ROW[${i+1}] field COUNT can't be blank`);
			if(parseInt(count) < 0)
				throw new Error(`ROW[${i+1}] field COUNT can't be negative`);
			//inventory
			if(inventory==undefined || inventory == "")
				throw new Error(`ROW[${i+1}] field INVENTORY can't be blank`);
			if(parseInt(inventory) < 0)
				throw new Error(`ROW[${i+1}] field INVENTORY can't be negative`);
			// set CELL[TOTAL]
			if( status == "delete" )  
				deletee+=1
			else if( status == "insert" )
				 	insert+=1
			else if( status == "update" ) 
			 		update+=1
		}
		return {
			deletee: deletee,
			insert: insert,
			update: update
		}
	}
	scwin.saveEventHandler= function(){
		try{
			var valid= scwin.checkValid()

			$p.executeSubmission("postDataShop");
		}catch (e) {
			alert(e.message);
		}
	}
