	scwin.setTotalRowCount= function() {
		// get total row
		var row= dl_Shoe_Shop.getRowCount()
		// set total text	
		anc_Total.setValue(`TOTAL: ${row}`)
	}

	scwin.initDataShop= function(){
	// get total row
		var row= dl_Shoe_Shop.getRowCount()
		// set total text	
		anc_Total.setValue(`TOTAL: ${row}`)
	// set CELL[NO] & CELL[TOTAL]
		for(let i= 0; i < row; i++){
			// get CELL[PRICE] & CELL[COUNT] value
			var status= dl_Shoe_Shop.getCellData(i, STATUS)
			var price= parseInt(dl_Shoe_Shop.getCellData(i, PRICE))
			var count= parseInt(dl_Shoe_Shop.getCellData(i, COUNT))
			var inventory= parseInt(dl_Shoe_Shop.getCellData(i, INVENTORY))
			// set CELL[TOTAL]
			//dl_Shoe_Shop.setCellData( i , TOTAL , price * count)
			//dl_Shoe_Shop.setCellData( i , NO , i + 1)
			//set allow to edit
			if (status == "insert") {
			}
			//set color for status of the INVENTORY
			 if(inventory>=50) 
			 	grv_Show_Figure.setCellColor(i, INVENTORY, "red")
			 else if(inventory<10)
			 	grv_Show_Figure.setCellColor(i, INVENTORY, "yellow")
			 else grv_Show_Figure.setCellColor(i, INVENTORY, "green")
			//grv_Show_Figure.set
		}

	}

	scwin.grv_Show_Figure_onafteredit = function(row,col,value) {
		try {
			var size= dl_Shoe_Shop.getCellData(row, SIZE)
			var price= dl_Shoe_Shop.getCellData(row, PRICE)
			var inventory= dl_Shoe_Shop.getCellData(row, INVENTORY)
			if(parseInt(size) > 50){
				dl_Shoe_Shop.setCellData(row, SIZE, "")
				throw new Error("SIZE can't large than 50")

			}	
			if(parseInt(size) < 0){
				dl_Shoe_Shop.setCellData(row, SIZE, "")
				throw new Error("SIZE can't be negative")

			}
			if(parseInt(price) < 0){
				dl_Shoe_Shop.setCellData(row, PRICE, "")
				throw new Error("SIZE can't be negative")

			}
			if(parseInt(inventory) < 0){
				dl_Shoe_Shop.setCellData(row, INVENTORY, "")
				throw new Error("INVENTORY can't be negative")

			}
		} catch (e) {
			alert(`Error: ${e.message}`)

		}

	};

	scwin.grv_Show_Figure_oncelldblclick = function(row,col,colId) {
		if( colId == STATUS )	{
			var status= dl_Shoe_Shop.getCellData(row, STATUS)
			//alert("status: "+ status)
			if (status == "update")
				dl_Shoe_Shop.setCellData(row, STATUS, "")		
			else if (status == "")
				dl_Shoe_Shop.setCellData(row, STATUS, "update")	
		}	
	};

