head.script:
// get page
	scwin.getHeaderPage= function() {
		return $p.parent().scwin.getHeaderPage()
	}

	scwin.getBodyPage= function() {
		return $p.parent().scwin.getBodyPage()
	}

	scwin.getFooterPage= function() {
		return $p.parent().scwin.getFooterPage()
	}
//       
	scwin.onpageload = function() {

	};

	scwin.onpageunload = function() {

	};

	scwin.hi = function(){
		alert("Call from footer")
		alert("Num: "+ $p.local.myData());
		alert("num2????????: " + $p.getComponentById("anc_Total").getValue())
	}

	scwin.trg_Add_onclick = function(e) {
		scwin.getBodyPage().scwin.addEventHandler()
	}

	scwin.trg_Delete_onclick = function(e) {
		if(!confirm("Are you sure to delete row?")) return
		scwin.getBodyPage().scwin.deleteEventHandler()
	};

	scwin.trg_Save_onclick = function(e) {
		if(!confirm("Are you sure to save the change?")) return
		scwin.getBodyPage().scwin.saveEventHandler()
	};
